sp_widget.client_script:
function($rootScope,$scope,$sce,spUtil) {
	/* widget controller */
	var c = this;
	// Use a position indicator to know which record to update
	c.notePos = 0;
	console.log(c.data);

	c.u_purpose_outline_summary = $sce.trustAsHtml(c.data.note.u_purpose_outline_summary.replace(/\n/g, '<br/>'));
	c.u_purpose_outline = $sce.trustAsHtml(c.data.note.u_purpose_outline.replace(/\n/g, '<br/>'));
	c.u_target_person = $sce.trustAsHtml(c.data.note.u_target_person.replace(/\n/g, '<br/>'));
	c.u_contact_information = $sce.trustAsHtml(c.data.note.u_contact_information.replace(/\n/g, '<br/>'));

}

sp_widget.script:
(function() {

	data.note = {};
	var sys_id = $sp.getParameter("sys_id")
	var noteGR = new GlideRecord('pc_product_cat_item');
	noteGR.addQuery('model', 'bd835b5d1b055010f61b4196bc4bcbac');
	noteGR.addQuery('sys_id', sys_id);
	noteGR.orderBy('name');
	noteGR.setLimit(options.maximum_records_to_display);
	noteGR.query();
	if (noteGR.next()) {
		$sp.getRecordDisplayValues(data.note, noteGR, 'short_description,u_purpose_outline_summary,u_purpose_outline,u_target_person,u_contact_information,u_recruitment_period,sys_id');
		data.note.name = noteGR.getValue('name');
	}

	var attachGR = new GlideRecord('sys_attachment');
	attachGR.addQuery('table_name', 'pc_product_cat_item');
	attachGR.addQuery('table_sys_id', sys_id);
	attachGR.setLimit(1);
	attachGR.query();
	if (attachGR.next()) {
		data.note.attach = attachGR.getValue('sys_id');
		data.note.file_name = attachGR.getValue('file_name');
	}
}
)();
